    "process.hero.title": { en: "A-to-Z Service in", nl: "A-tot-Z Service in", de: "A-bis-Z Service in", fr: "Service de A à Z en", es: "Servicio de A a Z en", it: "Servizio dalla A alla Z in", pl: "Usługa od A do Z w" },
